string getEstadoNonTerminal(int estado, string str) {
    for (int coluna = 32; coluna < COLUNAS; coluna++ )
        if (str == tableLR[0][coluna])
            return tableLR[estado][coluna];
    return "false";
}

bool ehPalavraReservada(string lexema){
    for (int i = 0; i < pvrsReservadas.size(); i++)
        if (pvrsReservadas[i] == lexema)
            return true;
    
    return false;
}

bool ehTipo(string lexema) {
    if (lexema == "real" || lexema == "int" || lexema == "letra")
        return true;
    return false;
}

string getAcao(string str, int estado) {
    int col;
    
    for (col = 1; col <= 31; col++)
        if (str == tableLR[0][col])
            return tableLR[estado][col];

    return "false";
}

bool analisadorSintatico(list<Lexema> entrada) {
    stack<string> pilha;
    string acao;
    int estado = 0;
    Lexema lexema;
    
    lexema.str = "$";
    entrada.push_back(lexema);
    pilha.push("$");
    pilha.push("0");

    while(!pilha.empty() && !entrada.empty()) {
        lexema = entrada.front();
        acao = getAcao(lexema.str, estado + 1);

        if ((acao == "false") || (acao == "#"))
            return false;

        if (acao[0] == 's') {
            pilha.push(lexema.str);
            pilha.push(acao.substr(1));
            entrada.pop_front();
            estado = stoi(acao.substr(1));
        }

        if (acao[0] == 'r') {
            int idcRegra = stoi(acao.substr(1));
            if  (idcRegra == 22 || idcRegra == 28 || idcRegra == 32 || idcRegra == 46) {
                pilha.push(gramatica[idcRegra]);
                string str = getEstadoNonTerminal(estado, gramatica[idcRegra]);
                if (str == "acc")
                    return true;
                if (str == "false" || str == "#")
                    return false;
                estado = stoi(str);
                pilha.push(str);
            } else {
                int numDePops = (qtdRegras[idcRegra] * 2);
                for (int i = 0; i < numDePops; i++)
                    pilha.pop();
                estado = stoi(pilha.top());
                pilha.push(gramatica[idcRegra]);
                string str = getEstadoNonTerminal(estado, gramatica[idcRegra]);
                if (str == "acc")
                    return true;
                if (str == "false" || str == "#")
                    return false;
                estado = stoi(str);
                pilha.push(str);
            }
        }
    }

    return true;
}

/*Funções do Léxico */

bool is_charValido(char c){
    return (c == 9 || c == 10 || (c >= 32 && c <= 126));
}

bool is_letterUnder(char c){  
    return ((c >= 65 && c <= 90) || (c >= 97 && c <= 122) || (c == 95));
}

bool is_number(char c){
    return (c >= 48 && c <= 57);
}

bool is_aspasDuplas(char c){
    return (c == 34);
}

bool is_aspasSimples(char c){
    return (c == 39);
}

bool is_ponto(char c){
    return (c == '.');
}

bool is_lexUnitario(char c){
    return ((c == 37) || (c == 42) || (c == 43) || (c == 45) || (c == 47) ||
            (c == 40) || (c == 41) || (c == 44) || (c == 59) || (c == 91) || 
            (c == 93) || (c == 123) || (c == 125));
}

bool is_exclamacao(char c){
    return (c == 33);
}

bool is_igual(char c){
    return (c == 61);
}

bool is_white(char c){
    return ((c == 9) || (c == 10) || (c == 32));
}

bool is_compOrdem(char c){
    return ((c == 60) || (c == 62));
}

bool is_EOF(char c){
    return (c == -1);
}

bool is_nenhumaColuna(char c){
    return !(is_letterUnder(c) || is_number(c) || is_aspasDuplas(c) || 
            is_aspasSimples(c) || is_ponto(c) || is_lexUnitario(c) || 
            is_exclamacao(c) || is_igual(c) || is_white(c) || is_compOrdem(c) || 
            is_EOF(c));
}

