int qzero(char c) {
    if (is_letterUnder(c)) {
        consome(c);
        return ID;
    } else if (is_number(c)) {
        consome(c);
        return NUM_I;
    } else if (is_aspasDuplas(c)) {
        consome(c);
        return STR;
    } else if (is_aspasSimples(c)) {
        consome(c);
        return PRE_CHR;
    } else if (is_ponto(c) || is_nenhumaColuna(c)) {
        return Q0;
    } else if (is_lexUnitario(c)) {
        consome(c);
        gerarLexema();
        return Q0;
    } else if (is_exclamacao(c)) {
        consome(c);
        return PRE_DIF;
    } else if (is_igual(c)) {
        consome(c);
        return ATRIB;
    } else if (is_white(c)) {
        return Q0;
    } else if (is_compOrdem(c)) {
        consome(c);
        return CMP_ORD1;
    } else if (is_EOF(c)) {
        return Q0;
    }
}

